mapper.select:
select 

from HM_NormalDecodeData
where DataId = #{dataid,jdbcType=INTEGER}
  and DecodeTime = #{decodetime,jdbcType=TIMESTAMP}

mapper.delete:
delete from HM_NormalDecodeData
where DataId = #{dataid,jdbcType=INTEGER}
  and DecodeTime = #{decodetime,jdbcType=TIMESTAMP}

mapper.select:
select

from
(       )


    CurrentHeatNumber > #{gtHeatNumber}


    and CurrentHeatNumber < #{ltHeatNumber}


    and Wdc > #{gtWdc}


    and Wdc < #{ltWdc}


    and HeatPower > #{gtHeatPower}


    and HeatPower < #{ltHeatPower}


    and TotalFlow > #{gtTotalFlow}


    and TotalFlow < #{ltTotalFlow}


    and FlowRate > #{gtFlowrate}


    and FlowRate < #{ltFlowrate}
