mapper.select:
select 

from devlasteststate
where DtuId = #{dtuid,jdbcType=VARCHAR}
  and DevName = #{devname,jdbcType=VARCHAR}

mapper.delete:
delete from devlasteststate
where DtuId = #{dtuid,jdbcType=VARCHAR}
  and DevName = #{devname,jdbcType=VARCHAR}

mapper.insert:
insert into devlasteststate (DtuId, DevName, CurWaterFlow, 
  CurHeatNum, TotalWaterFlow, TotalHeatNum, 
  SupplyWaterTmp, ReturnWaterTmp, LastUpdateTime
  )
values (#{dtuid,jdbcType=VARCHAR}, #{devname,jdbcType=VARCHAR}, #{curwaterflow,jdbcType=REAL}, 
  #{curheatnum,jdbcType=REAL}, #{totalwaterflow,jdbcType=INTEGER}, #{totalheatnum,jdbcType=INTEGER}, 
  #{supplywatertmp,jdbcType=REAL}, #{returnwatertmp,jdbcType=REAL}, #{lastupdatetime,jdbcType=TIMESTAMP}
  )

mapper.update:
update devlasteststate
set CurWaterFlow = #{curwaterflow,jdbcType=REAL},
  CurHeatNum = #{curheatnum,jdbcType=REAL},
  TotalWaterFlow = #{totalwaterflow,jdbcType=INTEGER},
  TotalHeatNum = #{totalheatnum,jdbcType=INTEGER},
  SupplyWaterTmp = #{supplywatertmp,jdbcType=REAL},
  ReturnWaterTmp = #{returnwatertmp,jdbcType=REAL},
  LastUpdateTime = #{lastupdatetime,jdbcType=TIMESTAMP}
where DtuId = #{dtuid,jdbcType=VARCHAR}
  and DevName = #{devname,jdbcType=VARCHAR}
